def calculate_risk_score(threat_level, impact, likelihood):
    scores = {'Low': 1, 'Moderate': 2, 'High': 3}
    return scores[threat_level] * scores[impact] * scores[likelihood]
risk_score = calculate_risk_score('High', 'Moderate', 'Possible')
print('Risk Score:', risk_score)

def risk_matrix(likelihood, impact):
    matrix = {'High': {'Likely': 'Critical', 'Possible': 'High', 'Unlikely': 'Moderate'}, 'Moderate': {'Likely': 'High', 'Possible': 'Moderate', 'Unlikely': 'Low'}}
    return matrix[impact][likelihood]
